rule.example:
public void doSomething() {
  // this is OK
  if (foo) x++;

  // but this is not
  if (foo)
      x=x+1;
  else
      x=x-1;
}

public void foo() {
  for (int i=0; i<42;i++)
      foo();
}

public class Foo {
  public void bar() {
    int x = 0;
    if (foo) x++;
  }
}

public class Foo {
 public void bar() {
  int x=2;
  int y=3;
  int z=4;
  if (x>y) {
   if (y>z) {
    if (z==x) {
     // this is officially out of control now
    }
   }
  }
 }
}
