rule.example:
public class MaybeASingleton {
    public static void foo() {
     // etc
    }
    public static void bar() {
     // etc
    }
}

import java.util.*;
public class Bar {

 // should be "private List list"
 private ArrayList list = new ArrayList();

 // should be "public Set getFoo()"
 public HashSet getFoo() {
  return new HashSet();
 }
}

public class Foo {
  private int bar =2;
  public boolean isBarEqualsTo(int x) {
    // this bit of code
    if (bar == x) {
     return true;
    } else {
     return false;
    }
    // can be replaced with a simple
    // return bar == x;
  }
}

public class Foo {
 public void bar() {
  int x = 2;
  switch (x) {
   case 2: int j = 8;
  }
 }
}

public class Foo {
 public void bar() {
  int x=2;
  int y=3;
  int z=4;
  if (x>y) {
   if (y>z) {
    if (z==x) {
     // this is officially out of control now
    }
   }
  }
 }
}

public class Foo {
 private void foo(String bar) {
  bar = "something else";
 }
}


public class Foo {
  private int x;
  public void bar() {
    switch (x) {
      case 1: {
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        break;
      }

      case 2: {
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        break;
      }
    }
  }
}
