rule.example:
  
// Ok
function foo() {
   return 1;
}
// Bad
function bar() {
   var x = 1;
   return x;
   x = 2;
}

property.value:
 
//NumberLiteral[
	@Image != @Number
	and translate(@Image, "e", "E") != @Number
	and concat(@Image, ".0") != @Number
	and @Image != substring-before(translate(@Number, ".", ""), "E")]
